import React, {Component} from 'react'
import { useEffect, useState } from 'react';
import axios from 'axios';
import "./styles/Votes.css";
export default class Votes extends Component 
{
  constructor(props) 
    {
        super(props);
        this.state = 
        {
          votes : null
        }
    }
    componentDidMount() 
    {
        if(this.state.votes == null)
        {
            this.getVotes();
        }
        console.log(this.state.votes)
    }
    componentDidUpdate() 
    {
      
    }

    async getVotes()
    {
        const memberID = this.props.memberID
        var payload = {'memberID' : memberID}
       
        const params = new URLSearchParams(payload);
  
        let res = await axios.get(
          `http://127.0.0.1:8000/backend/votes?${params}`)
        
        this.setState({votes : res.data.results[0].votes})
    }

    getBillLink(element)
    {
     console.log('!!!!')
     console.log(element)
      var billID = element.split("-")
      billID = billID[0]
      console.log(billID)

      var x = billID.match(/[^\d]+|\d+/g);
      if(x[0] == 'pn')
      {
        console.log(x[0])
        console.log('https://www.congress.gov/nomination/117th-congress/' + x[1])
        return 'https://www.congress.gov/nomination/117th-congress/' + x[1];
      }
      else
      {
        console.log(x[0])
        console.log('https://www.govtrack.us/congress/bills/117/' + x[0] + x[1])
        return 'https://www.govtrack.us/congress/bills/117/' + x[0] + x[1];
      }

    }
   
    votes()
    {

        var bills = []
        {
             this.state.votes.forEach(element => {
              console.log(element)
                bills.push(
                <div className='bill'>
                  <div className= 'date-row'>
                    <p className='bill-date'>{element.date}</p>
                  </div>
                  <div className='first-row'>
                        <p className='bill-number'>{element.bill.number}</p>
                        
                  </div>
                        {element.bill.title ? 
                          <p className='bill-title'>Bill Title: {element.bill.title}</p> :
                          <p className='bill-title'>Bill Title: {element.description}</p>
                        
                      }

                        <p className='bill-question'>Question: {element.question}</p>
                        <p className='bill-result'>Result: {element.result}</p>
                        <p className='bill-total'>Total: {element.total.no}-{element.total.yes}</p>
                        <p className='bill-rep-position'>Position: {element.position}</p>    
                        <a href={this.getBillLink(element.bill.bill_id)}>More Information</a>     
                  </div>)
             })
        }
        console.log(bills)
        return <div className="bills">{bills}</div>;
    }
    render()
    {
        return(<div className="votes">
      {this.state.votes ? this.votes() : ''}</div>)
    }
}
