import React, {Component} from 'react'
import { useEffect, useState } from 'react';
import axios from 'axios';

import {Grid, Icon} from 'fomantic-ui'
export default class IndividualRepresentative extends Component 
{
  constructor(props) 
    {
        super(props);
        this.state = 
        {
          memberID : null
        }
    }

    async getMemberId()
      {
        
        const name = this.props.representative.official.name.split(' ')
        
        const firstname = name[0]
        const lastname = name[name.length -1]

        console.log(firstname, lastname, this.props.representative.ocd_id)
        var chamber = ''
        if(this.props.representative.title == 'U.S. Representative')
        {
          chamber = 'house'
        }
        else
        {
          chamber = 'senate'
        }
        var payload = {'firstname' : firstname, 'lastname' : lastname, 'ocd_id' : this.props.representative.ocd_id, 'chamber': chamber}
       
        const params = new URLSearchParams(payload);
  
        let res = await axios.get(
          `http://127.0.0.1:8000/backend/memberID?${params}`)
        
        this.setState({memberID : res.data})
      }
      getLink()
      {
        return 'https://raw.githubusercontent.com/unitedstates/images/gh-pages/congress/225x275/'+ this.state.memberID + '.jpg'
      }
      componentDidMount() 
      {
        if(this.state.memberID == null)
        {
          this.getMemberId();
        }
      }
      componentDidUpdate() 
      {
        //{this.state.memberID ? <div><p>Name {this.props.representative.official.name}</p> <img src={this.getLink()}></img></div> : 'test'}
      }
    render()
    {
    return(
      <Grid celled>
      <Grid.Row>
        <Grid.Column width={7}>
          <Icon name='node'/>
        </Grid.Column>
        <Grid.Column width={8}>
          <Icon name='html5'/>
        </Grid.Column>
      </Grid.Row>
    
      <Grid.Row>
        <Grid.Column width={5}>
          <Icon name='js'/>
        </Grid.Column>
        <Grid.Column width={5}>
          <Icon name='react'/>
        </Grid.Column>
        <Grid.Column width={5}>
          <Icon name='angular'/>
        </Grid.Column>
      </Grid.Row>
    </Grid>
  )}

}
