import React, {Component} from 'react'
import { useEffect, useState } from 'react';
import axios from 'axios';

export default class IndividualRepresentative extends Component 
{
  constructor(props) 
    {
        super(props);
    }
    componentWillMount() 
    {
      getMemberId()
    }
    componentDidUpdate() 
    {
      
    }
      async getMemberId()
      {
        
        const name = props.representative.official.name.split(' ')
        
        const firstname = name[0]
        const lastname = name[name.length -1]

        console.log(firstname, lastname, props.representative.ocd_id)
        var chamber = ''
        if(props.representative.title == 'U.S. Representative')
        {
          chamber = 'house'
        }
        else
        {
          chamber = 'senate'
        }
        var payload = {'firstname' : firstname, 'lastname' : lastname, 'ocd_id' : props.representative.ocd_id, 'chamber': chamber}
       
        const params = new URLSearchParams(payload);
  
        let res = await axios.get(
          `http://127.0.0.1:8000/backend/memberID?${params}`)
        
        console.log(res)
        return res
      }
    render(){
    return(
    <div><p>Name {props.representative.official.name} {console.log('bingg')}</p></div>
  )}
}
