import React, { Component } from 'react'
import logo from '../images/logo.png'

export default class Startup extends React.Component {
    constructor(props) {
      super(props);
      this.state = 
      {
          street: '', 
          city: '', 
          zipcode: ''
        };
  
      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
    }
  
    handleChange(event) 
    {
        this.setState({[event.target.name]: event.target.value});
    }

  
    handleSubmit(event) 
    {
        this.props.setConstituent(this.state);
       
        event.preventDefault();
    }
  
    render() {
      return (
        <div>
        <img src ={logo}/>
        <form onSubmit={this.handleSubmit}>
          <label>
            Street:
            <input name="street" type="text" value={this.state.street} onChange={this.handleChange} />
          </label>
          <label>
            City:
            <input name="city" type="text" value={this.state.city} onChange={this.handleChange} />
          </label>
          <label>
            ZipCode:
            <input name="zipcode" type="text" value={this.state.zipcode} onChange={this.handleChange} />
          </label>
          <input type="submit" value="Submit" />
        </form>
        </div>
      );
    }
  }
