import React, { Component } from 'react'
import logo from '../images/logo.png'


export default class Startup extends React.Component {
    constructor(props) {
      super(props);
      this.state = 
      {
          street: '', 
          city: '', 
          zipcode: '',
        };
      

  
      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
    }
  
    handleChange(event) 
    {
        this.setState({[event.target.name]: event.target.value});
        console.log(this.query);
    }

  
    handleSubmit(event) 
    {
        this.props.setConstituent(this.state);

        event.preventDefault();
    }



loadScript = (url, callback) => {
  let script = document.createElement("script");
  script.type = "text/javascript";

  if (script.readyState) {
    script.onreadystatechange = function() {
      if (script.readyState === "loaded" || script.readyState === "complete") {
        script.onreadystatechange = null;
        callback();
      }
    };
  } else {
    script.onload = () => callback();
  }

  script.src = url;
  document.getElementsByTagName("head")[0].appendChild(script);
};

handleScriptLoad(updateQuery, autoCompleteRef) {
  autoComplete = new window.google.maps.places.Autocomplete(
    autoCompleteRef.current,
    { types: ['address'], componentRestrictions: { country: "us" } }
  );
  autoComplete.setFields(["address_components", "formatted_address"]);
  autoComplete.addListener("place_changed", () =>
    handlePlaceSelect(updateQuery)
  );
}

handlePlaceSelect(updateQuery) {
  const addressObject = autoComplete.getPlace();
  const query = addressObject.formatted_address;
  updateQuery(query);
  console.log(addressObject);
}

SearchLocationInput() {
  const [query, setQuery] = useState("");
  const autoCompleteRef = useRef(null);

  useEffect(() => {
    loadScript(
      `https://maps.googleapis.com/maps/api/js?key=AIzaSyDRZuEFP9Y9rUvs0gJsqB7KDmaHPnv8rTo&libraries=places`,
      () => handleScriptLoad(setQuery, autoCompleteRef)
    );
  }, []);
}

  
    render() {
      return (
        
        <div>
          <LocationSearchInput></LocationSearchInput>
        <img src ={logo}/>
        <form onSubmit={this.handleSubmit}>
          <label>
            Street:
            <input name="street" type="text" value={this.state.street} onChange={this.handleChange} />
          </label>
          <br />
          <label>
            City:
            <input name="city" type="text" value={this.state.city} onChange={this.handleChange} />
          </label>
          <br /> 
          <label>
            ZipCode:
            <input name="zipcode" type="text" value={this.state.zipcode} onChange={this.handleChange} />
          </label>
          <br />
          <input type="submit" value="Submit" />
        </form>
        </div>
      );
    }
  }
