import React, { Component } from 'react'
import backIcon from '../images/Back-Icon.png'


export default class Representatives extends Component 
{
  constructor(props) 
    {
        super(props);
        /*this.state = 
        {
            street: '', 
            city: '', 
            zipcode: ''
          };
    
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);*/
      } 
  GetRepNames()
  {
    return fetch("/api/get-room" + "?code=" + this.roomCode)
    .then((response) => {
      if (!response.ok) {
        this.props.leaveRoomCallback();
        this.props.history.push("/");
      }
      return response.json();
    })
    .then((data) => {
      this.setState({
        votesToSkip: data.votes_to_skip,
        guestCanPause: data.guest_can_pause,
        isHost: data.is_host,
      });
      if (this.state.isHost) {
        this.authenticateSpotify();
      }
    });
}
  componentDidMount() 
  {
        
  }
  componentDidUpdate() 
  {
        
  }
  render() 
  {
    return (
      <div>Representatives Page
    <button><img src={backIcon} alt="my image" onClick={() => this.props.setConstituent(null)} /></button>
    <div> Address
          <p>
              {this.props.constituent.street} St. {this.props.constituent.city} {this.props.constituent.zipcode}
        </p>
    </div>
        <div>Senator 1</div>
        <div>Senator 2</div>
        <div>House Representative</div>
      </div>
   
    )
  }
}
