import React, { Component } from 'react'
import backIcon from '../images/Back-Icon.png'
import axios from 'axios'
import IndividualRepresentative from './IndividualRepresentative';

export default class Representatives extends Component 
{
  constructor(props) 
    {
        super(props);

        this.state = 
        {
            senator1 : {},
            senator2 : {},
            houserep : {},   
        };
    
 
 
      } 
  async GetRepNames()
  {

 
    let payload = {'street': this.props.constituent['street'], 'city' : this.props.constituent['city'], 'zipcode' : this.props.constituent['zipcode']}

    const params = new URLSearchParams(payload);

    let res = await axios.get(
      `http://127.0.0.1:8000/backend/representatives?${params}`)
  console.log(res);
      this.state.houserep = {
        district: res.data.house.district, 
        official: res.data.house.officials[0],
        title: res.data.house.title
      }
      this.state.senator1 = {
        district: res.data.senators.district, 
        official: res.data.senators.officials[0],
        title: res.data.senators.title
      }
      this.state.senator2 = {
        district: res.data.senators.district, 
        official: res.data.senators.officials[1],
        title: res.data.senators.title
      }
  
    
  }
  componentDidMount() 
  {
    this.GetRepNames() 
  }
  componentDidUpdate() 
  {
    this.GetRepNames()
  }
  render() 
  {
    return (
      <div>Representatives Page
    <button><img src={backIcon} alt="my image" onClick={() => this.props.setConstituent(null)} /></button>
    <div> Address
          <p>
              {this.props.constituent.street} St. {this.props.constituent.city} {this.props.constituent.zipcode}
        </p>
    </div>
    
      {this.state.senator2 ? '' : <div>House rep <IndividualRepresentative representative = {this.state.houserep}></IndividualRepresentative>
Senator 1 <IndividualRepresentative representative = {this.state.senator1}></IndividualRepresentative>
        Senator 2 <IndividualRepresentative representative = {this.state.senator2}></IndividualRepresentative></div> }
   
      </div>
   
    )
  }
}
