import React, { Component } from 'react'
import backIcon from '../images/Back-Icon.png'
import axios from 'axios'
import IndividualRepresentative from './IndividualRepresentative';

export default class Representatives extends Component 
{
  constructor(props) 
    {
        super(props);

        this.state = 
        {
            senator1 : null,
            senator2 : null,
            houserep : null,   
        };
    
 
 
      } 
  async GetRepNames()
  {

 
    let payload = {'street': this.props.constituent['street'], 'city' : this.props.constituent['city'], 'zipcode' : this.props.constituent['zipcode']}

    const params = new URLSearchParams(payload);
    console.log('bing4523523')
    let res = await axios.get(
      `http://127.0.0.1:8000/backend/representatives?${params}`)
  console.log(res);
      this.setState({houserep : {
        district: res.data.house.district, 
        official: res.data.house.officials[0],
        title: res.data.house.title,
        ocd_id: res.data.house.ocd_id
      }, senator1 : {
        district: res.data.senators.district, 
        official: res.data.senators.officials[0],
        title: res.data.senators.title,
        ocd_id: res.data.senators.ocd_id
      },senator2 : {
        district: res.data.senators.district, 
        official: res.data.senators.officials[1],
        title: res.data.senators.title,
        ocd_id: res.data.senators.ocd_id
      }})


      console.log(this.state.houserep)
      console.log(this.state.senator1)
      console.log(this.state.senator2)
  
    
  }
  componentDidMount() 
  {
    console.log('bing')
    this.GetRepNames() 
  }
  componentDidUpdate() 
  {
    
  }
  render() 
  {
    return (
<div>Representatives Page
      <button><img src={backIcon} alt="my image" onClick={() => this.props.setConstituent(null)} /></button>
      <div> Address
        <p>
          {this.props.constituent.street} St. {this.props.constituent.city} {this.props.constituent.zipcode}
        </p>
      </div>
    
      {this.state.houserep ? <div class="ui internally celled grid">
        <div class="row">
          <div>House rep <IndividualRepresentative representative = {this.state.houserep}></IndividualRepresentative></div>
        </div>
        <div class="row">
          <div>Senator 1 <IndividualRepresentative representative = {this.state.senator1}></IndividualRepresentative></div>
        </div>
        <div class="row">
          <div>Senator 2 <IndividualRepresentative representative = {this.state.senator2}></IndividualRepresentative></div>
        </div> </div>: 'test'}
   
      </div>
   
    )
  }
}
