import React, {Component} from 'react'
import { useEffect, useState } from 'react';
import axios from 'axios';


export default class Votes extends Component 
{
  constructor(props) 
    {
        super(props);
        this.state = 
        {
          votes : null
        }
    }
    componentDidMount() 
    {
        if(this.state.votes == null)
        {
            this.getVotes();
        }
        console.log(this.state.votes)
    }
    componentDidUpdate() 
    {
      
    }

    async getVotes()
    {
        const memberID = this.props.memberID
        var payload = {'memberID' : memberID}
       
        const params = new URLSearchParams(payload);
  
        let res = await axios.get(
          `http://127.0.0.1:8000/backend/votes?${params}`)
        
          console.log(res.data)
        this.setState({votes : res.data.results[0].votes})
    }

    votes()
    {

        var bills = []
        {
            this.state.votes.forEach(element => {
                bills.push(
                <li>
                        <p>Bill Number: {element.bill.number}</p>
                        <p>Bill Title: {element.description}</p>
                        <p>Date: {element.date}</p>
                        <p>Question: {element.question}</p>
                        <p>Result: {element.result}</p>
                        <p>Total: {element.total.no}-{element.total.yes}</p>
                        <p>Position: {element.position}</p>               
                </li>)
             })
        }
        console.log(bills)
        return <ul>{bills}</ul>;
    }
    render()
    {
        return(<div>
      {this.state.votes ? this.votes() : ''}</div>)
    }
}
